import React from 'react';
import styles from './App.css';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';


const axios = require('axios');

class MainComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "",
      output: ""
    };
  }

  handleChange = (event) => {
    this.setState({ input: event.target.value})
  }

  handleClick = (event) => {
    const {input} = this.state;
    axios.post('https://jsonplaceholder.typicode.com/todos/1', {
        "input_text": input
    })
    .then((response) => {
        console.log(response);
        this.setState({output: "Result here"});
    })
    .catch(function (error) {
        console.log(error);
    })

    event.preventDefault();
  }

  render() {
    let rows = [];

    return (
      <div>
        <form>
            <label>Enter your question</label>
            <input placeholder="Enter a text" type="text" onChange={this.handleChange} 
                value={this.state.input}/>
            <label>SQL Query</label>
            <textarea disabled={true} type="text" rows="4" value={this.state.output}/>
            <button onClick={this.handleClick}>Submit</button> 
        </form>
        <TableContainer className={styles.sqltable} component={Paper}>
          <Table aria-label="simple table">
            <TableHead>
              <TableRow>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (
                <TableRow key={row.name}>
                  <TableCell component="th" scope="row">
                    {row.name}
                  </TableCell>
                  <TableCell align="right">{row.calories}</TableCell>
                  <TableCell align="right">{row.fat}</TableCell>
                  <TableCell align="right">{row.carbs}</TableCell>
                  <TableCell align="right">{row.protein}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    );
  }
}

export default MainComponent;
