const child = window.require('child_process');
const electron = window.require('electron');
const Path = require('path');
import React from 'react';
import styles from '../styles/common.css';
import { WINDOWS, LINUX } from '../constants';
import CollectorConfig from '../configs/collector.config';
import fs from 'fs';
import collectorConfig from '../configs/collector.config';
const { ipcRenderer } = require('electron');
const errorFile = Path.join(electron.remote.app.getAppPath(), '/error.log');

class Connect extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ipaddress: '',
      port: '',
      username: '',
      password: "",
      noIpaddress: false,
      noPort: false,
      noUsername: false,
      noPassword: false,
      showError: false,
      errorMessage: 'Please fill in all the fields.',
      buttonDisabled: false,
      vms: [],
      checked: [],
      datacenters: [],
      clusters: [],
      source: 'Please Select',
    };
    this.jsonDataChunks = [];
  }

  render() {
    return (
        <form>
            <label>Enter your question:</label>
            <input placeholder="Enter a text" type="text"/>
            <label>SQL Query:</label>
            <textarea disabled="true" type="text" rows="4"/>
            <button onClick={this.handleClick}>Submit</button> 
      </form>
    );
  }
}

export default Connect;
